if (typeof define !== 'function') {
    var define = require('amdefine')(module);
}

var Routes = {
            ///////
            // User
                _USER                           : "/user",
                _USER_userId                    : "/user/:userId",
            // UserAssociation
                _USER_userId_ASSOCIATION_aId    : "/user/:userId/association/:aId",
            // UserAssociations
                _USER_userId_ASSOCIATIONS       : "/user/:userId/associations",
            //////////////
            // Association
                _ASSOCIATION                    : "/association",
                _ASSOCIATION_aId                : "/association/:aId",
            // AssociationAdmin
                _ASSOCIATION_aId_ADMIN_userId   : "/association/:aId/admin/:userId",
            // AssociationAmins
                _ASSOCIATION_aId_ADMINS         : "/association/:aId/admins"            };

define( function() {

    var exports = {};
    exports.generate = function(route, descriptorObject) {
        for (var prop in descriptorObject) {
            if (descriptorObject.hasOwnProperty(prop))
                if (route.containsString(prop))
                    route = route.replace(prop, descriptorObject[prop]);
        }
        return route;
    }

    exports.getRoutes = function() {
        return Routes;
    }

    return exports;

});

