if (typeof define !== 'function') {
    var define = require('amdefine')(module);
}

var Routes = {
            ///////
            // User
                _USER                   : "/user",
                _USER__ID               : "/user/:id",
            // UserAssociation
                _USER__ID_ASSOCIATION   : "/user/:id/association",
            // UserAssociations
                _USER__ID_ASSOCIATIONS  : "/user/:id/associations",
            //////////////
            // Association
                _ASSOCIATION            : "/association",
                _ASSOCIATION__ID        : "/association/:id",
            // AssociationAdmin
                _ASSOCIATION__ID_ADMIN  : "/association/:id/admin",
            // AssociationAmins
                _ASSOCIATION__ID_ADMINS : "/association/:id/admins" };

define( function() {

    var exports = {};
    exports.generate = function(route, descriptorObject) {
        for (var prop in descriptorObject) {
            if (descriptorObject.hasOwnProperty(prop))
                if (route.containsString(prop))
                    route = route.replace(prop, descriptorObject[prop]);
        }
        return route;
    }

    exports.getRoutes = function() {
        return Routes;
    }

    return exports;

});

