if (typeof define !== 'function') {
    var define = require('amdefine')(module);
}

            // General
var Routes = {  _HOME :                     "/",
                _USER_REGISTER :            "/register",
                _LOGIN_POST :               "/login",
                _USER_LOGOUT :              "/logout",
                _ASSOCIATION_REGISTER :     "/register/association",

            // User
                __USER_PROFILE :            "/:username",
                __USER_ACCOUNT :            "/:username/account",
                __USER_ACCOUNT_UPDATE :     "/:username/update",
                __USER_LANG_UPDATE_POST :   "/:username/update/lang",

            // Association
                __ASSOCIATION_PROFILE :     "/association/:name",
                __ASSOCIATION_ACCOUNT :     "/association/:name/account",
                __ASSOCIATION_FOLLOW :      "/association/:name/follow",
                __ASSOCIATION_UNFOLLOW :    "/association/:name/unfollow"   };

define( function() {

    var exports = {};
    exports.generate = function(route, descriptorObject) {
        for (var prop in descriptorObject) {
            if (descriptorObject.hasOwnProperty(prop))
                if (route.containsString(prop))
                    route = route.replace(prop, descriptorObject[prop]);
        }
        return route;
    }

    exports.getRoutes = function() {
        return Routes;
    }

    return exports;

});

