if (typeof define !== 'function') {
    var define = require('amdefine')(module);
}

define([
    'child_process',
    'path',
    'module'
], function (child_process, path, module) {

    var exports         = {},
        exec            = child_process.exec,
        scssdir         = path.dirname(module.uri) + '/public/stylesheets/scss/',
        stylesheetsdir  = path.dirname(module.uri) + '/public/stylesheets/';

    exports.compile = function(filename) {
        exec('sass ' + scssdir + filename + '.scss ' + stylesheetsdir + filename + '.css', function (err, stdout, stderr) {
            console.log('SCSS file compiled - ' + filename);
        });
    }

    return exports;

});



