if (typeof define !== 'function') {
    var define = require('amdefine')(module);
}

var Dictionnary = {
    globals : null,
    lang_fr : null,
    lang_en : null
}

define([
    'lang/globals',
    'lang/en',
    'lang/fr'
], function(globals, en, fr) {

    Dictionnary.globals = globals;
    Dictionnary.fr = en;
    Dictionnary.en = fr;

    exports.getLanguage = function(lang) {
        return {
            lang: Dictionnary[ lang ],
            globals: Dictionnary.globals
        };
    }

    return exports;
});